mapper.select:
		select count(*)
				from video 			
   		  where category_code = #{cateCode}

mapper.update:

   			select max(reply_number) 
   			from video_reply 
   			where video_number = #{video_number}

		update video_reply 
			set reply_orgin_number = #{reply_number}
		where reply_number = #{reply_number}

update video_reply 
	set reply_content = #{reply_content}
where reply_number = #{reply_number}
