mapper.select:
		select count(*)
				from video 			
   		  where category_code = #{cateCode}

mapper.update:

   			select max(reply_number) 
   			from video_reply 
   			where video_number = #{video_number}

		update video_reply 
			set reply_orgin_number = #{reply_number}
		where reply_number = #{reply_number}

update video_reply 
	set reply_content = #{reply_content}
where reply_number = #{reply_number}

mapper.select:
SELECT v.video_number,v.user_id,v.category_code,v.video_title,v.public_check,v.video_reports,v.video_insert_date,v.video_rank_limit, vf.orgin_video_name,vf.stored_video_name,vf.orgin_video_thumb,vf.stored_video_thumb, (SELECT count(vr.reply_number) FROM video_reply vr WHERE vr.video_number = v.video_number) AS 'reply_count' FROM video v JOIN video_file vf ON v.video_number = vf.video_number WHERE v.category_code = #{cateCode}
AND v.video_title LIKE CONCAT ('%' ,#{keyword}, '%')
ORDER BY v.video_number DESC LIMIT #{pageStart},#{perPageNum}
